var updateCounter = function(t) {
		var o = new AV.Query("Counter");
		o.equalTo("url", location.pathname), o.first().then(function(o) {
			if (o) o.set("time", o.get("time") + 1), o.save().then(function(t) {
				console.log("update a counter:" + t.get("url"))
			}, function(t) {
				console.log("Error: " + t.code + " " + t.message)
			});
			else {
				var e = new t;
				e.set("url", location.pathname), e.set("time", 1), e.save().then(function(t) {
					console.log("add a counter:" + t.get("url"))
				}, function(t) {
					console.log("Error: " + t.code + " " + t.message)
				})
			}
		}, function(t) {
			console.log("Error: " + t.code + " " + t.message)
		})
	},
	showTime = function(t) {
		var o = new AV.Query("Counter");
		$(".visit").each(function() {
			var t = $(this).data("url"),
				e = $(this);
			o.equalTo("url", t), o.first().then(function(t) {
				var o = 0;
				t && (o = t.get("time")), e.find(".visit-count").text(o)
			}, function(t) {
				console.log("Error: " + t.code + " " + t.message)
			})
		})
	};
	
