	var o = '[data-toggle="totop"]',
		e = '[data-toggle="sidebar"]',
		n = '[data-toggle="menu"]',
		a = function() {
			t("html, body").animate({
				scrollTop: 0
			}, 200)
		},
		i = function(o) {
			t("body").toggleClass("fold")
		},
		l = function(o) {
			var e = t(o.target).closest('[data-toggle="menu"]'),
				n = t(e.data("target"));
			n.toggleClass("open")
		};
	t(document).on("click.totopToggle", o, a), t(document).on("click.sidebarToggle", e, i), t(document).on("click.menuToggle", n, l)
